import { assign } from '../src/assign';

describe('Assign', () => {
	it('should merge object', () => {
		let obj = assign({a: 1}, {b: 2})
		Object.keys(obj).length.should.equal(2);
		obj.a.should.equal(1);
		obj.b.should.equal(2);
	});

	it('should support symbol assign', () => {
		let s = Symbol();
		let obj = assign({[s]: 1}, {b: 2})
		Object.keys(obj).length.should.equal(1);
		Object.getOwnPropertySymbols(obj).length.should.equal(1);
		obj[s].should.equal(1);
		obj.b.should.equal(2);
	});
});
