import * as types from '../src/type';

describe('Type', () => {
	it('isArray', () => {
		types.isArray([1,2,3]).should.be.true;
		types.isArray('[1,2,3]').should.be.false;
	});

	it('isNumber', () => {
		types.isNumber(123).should.be.true;
		types.isNumber('321').should.be.false;
	});

	it('isFunction', () => {
		types.isFunction(() => {}).should.be.true;
		types.isFunction('321').should.be.false;
	});

	it('isObject', () => {
		types.isObject({}).should.be.true;
		types.isObject(() => {}).should.be.false;
	});

	it('isString', () => {
		types.isString('1231231aaaa').should.be.true;
		types.isString({}).should.be.false;
	});

	it('isBoolean', () => {
		types.isBoolean(true).should.be.true;
		types.isBoolean('true').should.be.false;
	});

	it('isNull', () => {
		types.isNull(null).should.be.true;
		types.isNull(123123).should.be.false;
	});

	it('isUndefined', () => {
		var a;
		types.isUndefined(a).should.be.true;
		types.isUndefined(123123).should.be.false;
	});
});
