import { version, versionCompare } from '../src/version';

describe('Version', () => {
	describe('version()', () => {
		it ('should transfer string to object', () => {
			const v = version('1.2.3.678');
			v.full.should.equal('1.2.3.678');
			v.major.should.equal(1);
			v.minor.should.equal(2);
			v.patch.should.equal(3);
			v.build.should.equal(678);
		});
	});

	describe('versionCompare()', () => {
		it('should return greater', function () {
			const v1 = '1.2.3';
			const v2 = '1.4.2';
			versionCompare(v1, v2).should.equal(-1);
		});

		it('should get equal', function () {
			const v1 = '1.4.2';
			const v2 = '1.4.2';
			versionCompare(v1, v2).should.equal(0);
		});

		it('should return less', function () {
			const v1 = '3.2.3';
			const v2 = '1.4.2';
			versionCompare(v1, v2).should.equal(1);
		});

		it('should return equal', function () {
			versionCompare('5.0', '5.0.0').should.equal(0)
		})
	});
});
