import cookie from '../src/index';

describe('Cookie', () => {
	describe('set()', () => {
		it('should add cookie with key and val', () => {
			cookie.set('a', 1);
			cookie.set('b', 2);
			cookie.set('c', 3);
			document.cookie.should.equal('a=1; b=2; c=3');
		});
	});

	describe('get()', () => {
		it('should get cookie with key', () => {
			cookie.get('a').should.equal('1');
			cookie.get('b').should.equal('2');
			cookie.get('c').should.equal('3');
		});
	});

	describe('all()', () => {
		it('should get cookies', () => {
			const cookies = cookie.all();
			Object.keys(cookies).length.should.equal(3);
			cookies.a.should.equal('1');
			cookies.b.should.equal('2');
			cookies.c.should.equal('3');
		});
	});

	describe('remove()', () => {
		it('should remove cookie with given key', () => {
			cookie.remove('a');
			document.cookie.should.equal('b=2; c=3');
		});
	});

	describe('empty()', () => {
		it('should remove cookie with given key', () => {
			cookie.empty();
			document.cookie.should.equal('');
		});
	});
});
