const uglify = require('rollup-plugin-uglify');
const babel = require('rollup-plugin-babel');

let config = {
	entry: './src/index.js',
	targets: [
		{ format: 'umd', dest: './bundle/jm-webview.js' },
		{ format: 'es', dest: './bundle/jm-webview.es.js' },
	],
	moduleName: 'jm.webview',
	globals: {
		'@jmfe/jm-common': 'jm.common'
	},
	plugins: [
		babel()
	]
};

if (process.env.min) {
	config.targets.pop();
	config.targets[0].dest = './bundle/jm-webview.min.js';
	config.plugins.push(uglify());
}

export default config;
