import { nativeCall } from '../src/call';
require('chai').should();

describe('Call', () => {
	beforeEach(() => {
		global.window = { location: {} };
	});

	describe('nativeCall()', () => {
		it('should change the location with specific url', () => {
			nativeCall('virtual', {a: 1, b: 2});
			window.location.href.should.equal('openApp.jdMobile://virtual?params=%7B%22a%22%3A1%2C%22b%22%3A2%7D');
		});
	});
});
